import React, { Component } from 'react';

class Editable extends Component {

  constructor(props) {
    super(props)

    this.state = {
        value:"blabla", 
        isInEditMode:false,
    }

    this.changeEditMode = this.changeEditMode.bind(this)
    this.updateComponentValue = this.updateComponentValue(this)
    this.renderEditView = this.renderEditView.bind(this)
    this.renderDefaultView = this.renderDefaultView.bind(this)

}


//state = {
//  value : "Some text", 
//  isInEditMode: false
//}

changeEditMode = () => {
  this.setState(
    {
      isInEditMode:!this.state.isInEditMode
    }
  )
}

updateComponentValue= () => {
  this.setState({
    isInEditMode:false,
    value: this.refs.theTextInput.value
  })
}

renderEditView = () => {
  return <div>
    <input type="text" 
           defaultValue={this.state.value}
           ref="theTextInput"/>
  <button onClick={this.changeEditMode}>X</button>
  <button onClick={this.updateComponentValue}>OK</button></div>
}

renderDefaultView = () => {
  return <div onDoubleClick = {this.changeEditMode}> 
  {this.state.value}
</div>
}

  render() {
    return this.state.isInEditMode ? 
     this.renderEditView() : this.renderDefaultView()

  }
}

export default Editable;
