import React, { Component } from 'react';

class Bookmark extends Component {

  constructor(props) {
    super(props)

    if (this.props.value!="") {
      this.state = {
        value:this.props.value, 
        isInEditMode:false,
        docId:this.props.bookid,
        ref:this.props.bookref
      }
    } else {
      this.state = {
        value:"####", 
        isInEditMode:false,
        docId:this.props.bookid,
        ref:this.props.bookref
      }
    }

    this.changeEditMode = this.changeEditMode.bind(this)
    this.renderEditView = this.renderEditView.bind(this)
    this.renderDefaultView = this.renderDefaultView.bind(this)
    this.updateComponentValue = this.updateComponentValue.bind(this)


}


//state = {
//  value : "Some text", 
//  isInEditMode: false
//}

changeEditMode() {
  this.setState(
    {
      isInEditMode:!this.state.isInEditMode
    }
  )
}

updateComponentValue() {
  this.setState({
    isInEditMode:false,
    value: this.refs.theTextInput.value
  });
    this.state.ref.doc(this.state.docId).update({bookmark:this.refs.theTextInput.value});

}

renderEditView() {
  let urlCheckBtn = new URL("./check.png",import.meta.url);
  let urlCrossBtn = new URL("./nope.png",import.meta.url);

  return <div>
    <input type="text" 
           defaultValue={this.state.value}
           ref="theTextInput"/>
  <img onClick={this.changeEditMode} style={{height:20,width:20
}} src={urlCrossBtn}/> 
  <img onClick={this.updateComponentValue} style={{height:20,width:20
}} src={urlCheckBtn}/></div>
}

renderDefaultView() {
  return <div onDoubleClick = {this.changeEditMode}> 
  {this.state.value}
</div>
}

  render() {
    return this.state.isInEditMode ? 
     this.renderEditView() : this.renderDefaultView()

  }
}

export default Bookmark;
